const muiLoginForm = () => {
    const [values, setValues] = React.useState({
        name: "",
        password: "",

    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setValues({
            ...value
        });
    }

    return (
        <Container component="main" maxWidth="xs">
            <Box
            sx={{
                marginTop: 8,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
            >
                <Avatar sx={{m: 1, bgcolor:'secondary.main'}}>
                <LockOutlinedIcon />
                </Avatar>

                <Typography component="h1" variant="h5" sx={{mb: 3}}>
                    Sign up
                </Typography>

                <TextField
                margin="normal"
                label="Email Address" 
                required 
                fullWidth
                name="email" 
                autoFocus
                />
                <TextField
                margin="normal"
                label="Password" 
                type="password" 
                required 
                fullWidth
                name="password"
                autoComplete="current-password" 
                />
                <FormControlLabel 
                control={<Checkbox value="remember" color="primary"/>}
                label="Remember me" 
                />
                <Button 
                type="submit" 
                fullWidth
                variant="contained"
                sx={{mt: 3, mb:2}}
                >Sign in</Button>
                <Grid container>
                    <Grid item xs>
                        <Link>Forgot password?</Link>
                    </Grid>
                    <Grid item>
                        <Link to="/r2">Sign Up</Link>
                    </Grid>
                </Grid>
            </Box>
        </Container>
    )
}

export default muiLoginForm;
