import * as React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { Button, Stack, Grid, TextField, FormControlLabel, Checkbox } from '@mui/material';
import { styled } from '@mui/material/styles';

import { Container } from '@mui/system';
import Modal from 'react-modal';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

function Row(props) {
  const { row } = props;
  const [open, setOpen] = React.useState(false);

  const [checked, setChecked] = React.useState(false);

  const [okValue, setOkValue] = React.useState(3);

  const [modalIsOpen, setModalIsOpen] = React.useState(false);

  const [value, setValue] = React.useState(row.inter);
  //snackBar start
  const [state, setState] = React.useState({
    openSnack: false,
    vertical: 'top',
    horizontal: 'center',
  });

  const { vertical, horizontal, openSnack } = state;

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    else
      setState({ open: false, vertical: 'top', horizontal: 'center',});
  };

  //snackBar end 

  const handleChecked = (event) => {
    setChecked(event.target.checked);
  }

  const handleChange = (event) => {
    setValue(event.target.value);
  }

  const handleSudmit = (event) => {
    event.preventDefault();
    if(checked){
      setOkValue(4);
      const ok = 4;
      const date = new Date();
      const day = date.toLocaleDateString('ko-kr');
      const inter = value;
      const _id = row._id;
      React.usehandleSudmit(() => {
        fetch('http://127.0.0.1:5000/main/result', {
        credentials: 'include',    
        method: 'POST',
        headers: {
            'content-Type': 'application/json',
        },
        body: JSON.stringify({
          _id,
          ok,
          day,
          inter,
        }),
    })
      })
    }

    else {
      setOkValue(5);
      const ok = 5;
      const date = new Date();
      const day = date.toLocaleDateString('ko-kr');
      const inter = value;
      const _id = row._id;
      React.usehandleSudmit(() => {
        fetch('http://127.0.0.1:5000/main/result', {
        credentials: 'include',    
        method: 'POST',
        headers: {
            'content-Type': 'application/json',
        },
        body: JSON.stringify({
          _id,
          ok,
          day,
          inter,
        }),
    })
      })
    }
    setState({ openSnack: true,  vertical: 'top', horizontal: 'center',});
    setModalIsOpen(false);
}

  return (
    <React.Fragment>
      <StyledTableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
        <StyledTableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </StyledTableCell>

        <StyledTableCell component="th" scope="row">
          {row.name}
        </StyledTableCell>
        <StyledTableCell align="right">{row.sn}</StyledTableCell>
        <StyledTableCell align="right">{row.Classes}</StyledTableCell>
        <StyledTableCell align="right">
            {okValue === 3 ? "대기" : (okValue === 4 ? "재진" : "승인")}
        </StyledTableCell>
      </StyledTableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box sx={{ margin: 1 }}>
              <Typography variant="h6" gutterBottom component="div">
                진료결과
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell>날짜</TableCell>
                    <TableCell align="left">진료과</TableCell>
                    <TableCell align="left">환자증상</TableCell>
                    <TableCell align="left">처방내용</TableCell>
                    <TableCell align="right">처방여부</TableCell>
                  </TableRow>
                </TableHead>

                <TableBody>
                    <StyledTableRow>
                      <StyledTableCell component="th" scope="row">
                        {row.day}
                      </StyledTableCell>
                      <StyledTableCell align="left">{row.hospital}</StyledTableCell>
                      <StyledTableCell align="left">{row.symptom}</StyledTableCell>
                      <StyledTableCell align="left">{value}</StyledTableCell>
                      <StyledTableCell align="right">
                        <Stack direction="row-reverse" spacing={1} align="right">
                            <Button onClick={()=> setModalIsOpen(true)}>처방입력</Button>
                            <Modal className='container' isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
                            <Paper
                                sx={{
                                    p: 2,
                                    margin: 'auto',
                                    maxWidth: 500,
                                    flexGrow: 1,
                                    backgroundColor: (theme) =>
                                    theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
                                }}
                                >
                                <Grid
                                sx={{
                                    display: 'flex',
                                    flexDirection: 'column',
                                    alignItems: 'center',
                                }}>
                                    <TextField
                                    id="cont"
                                    label="신청 내용"
                                    multiline
                                    rows={12}
                                    name="inter"
                                    placeholder="두통약 처방"
                                    fullWidth
                                    sx={{mt : 3}}
                                    value={value}
                                    onChange={handleChange}
                                    />
                                  <FormControlLabel
                                  checked={checked}
                                  control={<Checkbox value="재진여부" color="primary"/>}
                                  label="재진여부 확인"
                                  onChange={handleChecked}
                                  />
                                <Button 
                                variant='contained' 
                                onClick={handleSudmit}
                                sx={{mt: 3}}>제출하기</Button>
                                </Grid>
                            </Paper>
                            </Modal>
                            <Snackbar anchorOrigin={{ vertical, horizontal }} open={openSnack} autoHideDuration={2000} onClose={handleClose}>
                            <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
                              제출에 성공하였습니다!
                            </Alert>
                          </Snackbar>
                        </Stack>
                      </StyledTableCell>
                    </StyledTableRow>
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

Row.propTypes = {
  row: PropTypes.shape({
    name: PropTypes.string.isRequired,
    Classes: PropTypes.string.isRequired,
    sn: PropTypes.string.isRequired,
    ok: PropTypes.number.isRequired,
    hospital: PropTypes.string.isRequired,
    symptom: PropTypes.string.isRequired,
    inter: PropTypes.string.isRequired,
    day: PropTypes.string.isRequired,
  }).isRequired,
};


