const muiLoginForm = () => {
    return (
        <div>
            <Avatar></Avatar>
            <LockOutlinedIcon />
            <Typography component="h1" variant="h5" sx={{mb: 3}}>
                Sign up
            </Typography>
            <TextField 
            label="Email Address" 
            required 
            fullWidth
            name="email" 
            autoFocus
            />
            <TextField 
            label="Password" 
            type="password" 
            required 
            fullWidth
            name="password"
            autoComplete="current-password" 
            />
            <FormControlLabel 
            control={<Checkbox value="remember" color="primary"/>}
            label="Remember me" 
            />
            <Button 
            type="submit" 
            fullWidth
            variant="contained"
            sx={{mt: 3}}
            >Sign in</Button>
            <Grid container>
                <Grid item xs>
                    <Link>Forgot password?</Link>
                </Grid>
                <Grid item>
                    <Link to="/r2">Sign Up</Link>
                </Grid>
            </Grid>
        </div>
    )
}

export default muiLoginForm;
