import * as React from 'react';
import { Grid, TextField, Typography, Button, Icon } from '@mui/material';
import {ReactComponent as Logo} from '../data/logo';
import { Link } from 'react-router-dom';
import { Container } from '@mui/system';
//mport { useNavigate } from "react-router-dom";
import "./layout.css";

//간부 페이지
export default function SignUpIndexV2(){
    //const navigate = useNavigate();

    const [values, setValues] = React.useState({
        id: "",
        password: "",
        name: "",
        ganbu: true
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setValues({
            ...values,
            [name]:value
        });
    }

    const onSubmitHandler = (event) => {
        event.preventDefault();
        const servNum = values.id;
        const password = values.password;
        const name = values.name;
        const ganbu = values.ganbu;
        fetch('http://127.0.0.1:5000/signup-cadre', {
            method: 'POST',
            headers: {
                'content-Type': 'application/json',
            },
            body: JSON.stringify({
                servNum,
                password,
                name,
                ganbu,
            }),
        });
    };


    return (
    <Container component="main" maxWidth>
        <Grid container sx={{borderBottom: 1, borderBottomColor: 'grey.500'}}>
            <Grid item xs>
                <Icon>
                    <Logo />
                </Icon>
            </Grid>

            <Grid item>
                <Link to="/">로그인</Link>
                 <sapn>  |  </sapn>
                <Link to="/signup-select">회원가입 </Link>
            </Grid>
        </Grid>

        <Grid
        id="아이콘 및 큰 제목"
        container
        sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: "center",}}
        >
            <Icon>
                <Logo />
            </Icon>
        <Typography variant="h5" component="h5">간부 회원가입</Typography>
        </Grid>

        <Container
        id="군번 및 비밀번호 입력"
        maxWidth="sm"
        sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: "center"
        }}
        >
            <TextField
            margin="normal"
            label="군번" 
            required 
            name="id"
            autoFocus
            onChange={handleChange} 
            variant="standard"
            fullWidth
            sx={{marginTop: 8}}
            />

            <TextField
            margin="normal"
            label="비밀번호" 
            //type="password" 
            required 
            fullWidth
            name="password"
            autoComplete="current-password" 
            onChange={handleChange} 
            variant="standard"
            />

            <TextField
            margin="normal"
            label="이름" 
            required 
            name="name"
            onChange={handleChange} 
            variant="standard"
            fullWidth
            sx={{marginTop: 8}}
            />

            <Button
                onClick={onSubmitHandler} 
                fullWidth
                variant="contained"
                size="large"
                sx={{mt: 2}}
                >
                    회원가입
                </Button>
        </Container>

    </Container>
    );
}
